import { PERSIST_OPTION, STORE_DEFAULT_NAME } from '../constants';
import { GlobalState, StateMachineOptions } from '../types';

function StoreFactory() {
  let options: StateMachineOptions = {
    name: STORE_DEFAULT_NAME,
    middleWares: [],
    persist: PERSIST_OPTION.ACTION,
  };
  let state: GlobalState = {};

  try {
    options.storageType =
      typeof sessionStorage !== 'undefined' ? window.sessionStorage : undefined;
  } catch {}

  return {
    updateStore(defaultValues: GlobalState) {
      try {
        state =
          (options.storageType &&
            JSON.parse(options.storageType.getItem(options.name!) || '')) ||
          defaultValues;
      } catch {
        state = defaultValues;
      }
    },
    saveStore() {
      options.storageType &&
        options.storageType.setItem(options.name!, JSON.stringify(state));
    },
    get state() {
      return state;
    },
    set state(value) {
      state = value;
    },
    get options() {
      return options;
    },
    set options(value) {
      options = value;
    },
  };
}

export default StoreFactory();
export const STORE_DEFAULT_NAME = '__LSM__';
export const STORE_ACTION_NAME = '__LSM_NAME__';
export const PERSIST_OPTION = {
  NONE: 'none',
  ACTION: 'action',
  UNLOAD: 'beforeUnload',
} as const;
import * as React from 'react';
import storeFactory from './logic/storeFactory';
import { StateMachineContextValue } from './types';
import { PERSIST_OPTION } from './constants';

type PropsChildren = {
  children?: React.ReactNode;
};

const StateMachineContext = React.createContext<StateMachineContextValue>(
  undefined as any,
);

export const StateMachineProvider: React.FC<PropsChildren> = ({ children }) => {
  const [state, setState] = React.useState(storeFactory.state);

  React.useEffect(() => {
    if (storeFactory.options.persist === PERSIST_OPTION.UNLOAD) {
      window.onbeforeunload = () => storeFactory.saveStore();
      storeFactory.options.storageType &&
        storeFactory.options.storageType.removeItem(storeFactory.options.name!);
    }
  }, []);

  return (
    <StateMachineContext.Provider value={{ state, setState }}>
      {children}
    </StateMachineContext.Provider>
  );
};

export const useStateMachineContext = () =>
  React.useContext<StateMachineContextValue>(StateMachineContext);
import * as React from 'react';
import { useStateMachineContext } from './StateMachineContext';
import storeFactory from './logic/storeFactory';
import {
  StateMachineOptions,
  GlobalState,
  AnyCallback,
  AnyActions,
  ActionsOutput,
} from './types';
import { PERSIST_OPTION, STORE_ACTION_NAME } from './constants';

export function createStore(
  defaultState: GlobalState,
  options?: StateMachineOptions,
) {
  if (options) {
    storeFactory.options = {
      ...storeFactory.options,
      ...options,
    };
  }

  if (process.env.NODE_ENV !== 'production') {
    if (typeof window !== 'undefined') {
      window.__LSM_NAME__ = storeFactory.options.name;
      window.__LSM_RESET__ = () =>
        storeFactory.options.storageType &&
        storeFactory.options.storageType.removeItem(storeFactory.options.name!);
    }
  }

  storeFactory.updateStore(defaultState);
}

const actionTemplate =
  <TCallback extends AnyCallback>(
    setState: React.Dispatch<React.SetStateAction<GlobalState>>,
    callback: TCallback,
  ) =>
  (payload: Parameters<TCallback>[1], options?: { skipRender: boolean }) => {
    if (process.env.NODE_ENV !== 'production') {
      window[STORE_ACTION_NAME] = callback.name;
    }

    storeFactory.state = callback(storeFactory.state, payload);

    if (storeFactory.options.middleWares) {
      storeFactory.state = storeFactory.options.middleWares.reduce(
        (currentValue, currentFunction) =>
          currentFunction(currentValue, callback.name, payload) || currentValue,
        storeFactory.state,
      );
    }

    (!options || !options.skipRender) && setState(storeFactory.state);

    if (storeFactory.options.persist === PERSIST_OPTION.ACTION) {
      storeFactory.saveStore();
    }
  };

export function useStateMachine<
  TCallback extends AnyCallback,
  TActions extends AnyActions<TCallback>,
>(
  actions?: TActions,
): {
  actions: ActionsOutput<TCallback, TActions>;
  state: GlobalState;
  getState: () => GlobalState;
} {
  const { state, setState } = useStateMachineContext();
  const actionsRef = React.useRef(
    Object.entries(actions || {}).reduce(
      (previous, [key, callback]) =>
        Object.assign({}, previous, {
          [key]: actionTemplate(setState, callback),
        }),
      {} as ActionsOutput<TCallback, TActions>,
    ),
  );

  return {
    actions: actionsRef.current,
    state,
    getState: React.useCallback(() => storeFactory.state, []),
  };
}
